import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import useTheme from "../hooks/useTheme";
import darkIcon from "../assets/dark_mode.svg";
import ligthIcon from "../assets/light_mode.svg";

export default function MobileNav() {
  const [isSearchOpen, setIsSearchOpen] = useState(false);

  const handleSearchClick = () => {
    setIsSearchOpen(true);
  };

  const handleBackClick = () => {
    setIsSearchOpen(false);
  };
  let { theme, changeTheme, isDark } = useTheme();
  let [search, setSearch] = useState();
  let navigate = useNavigate();
  let handleSearch = (e) => {
    e.preventDefault();
    navigate("/?search=" + search);
    setSearch("");
  };
  return (
    <div>
      <nav
        className={`block md:hidden  border border-b-2 shadow-lg ${
          isDark ? "bg-dbg border-primary shadow-rose-950" : ""
        }`}
      >
        {!isSearchOpen && (
          <ul className="flex justify-between items-center p-3 max-w-6xl  mx-auto">
            <li>
              <Link
                to={"/"}
                className="flex items-center gap-3 md:s-ml-32 cursor-pointer "
              >
                <span className="text-2xl font-bold text-primary ">
                  Book Store
                </span>
              </Link>
            </li>

            <li className="flex gap-3 items-center">
              <Link
                to={"/create"}
                className="text-white  px-2 py-2 rounded-2xl flex items-center gap-1"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  fill={isDark ? "" : ""}
                  viewBox="0 0 24 24"
                  strokeWidth={1.5}
                  stroke="currentColor"
                  className="size-8"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    d="M12 9v6m3-3H9m12 0a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z"
                  />
                </svg>
              </Link>
              <div>
                <button onClick={handleSearchClick}>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                    strokeWidth={1.5}
                    stroke={isDark ? "white" : "currentColor"}
                    className="size-6 mt-2"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"
                    />
                  </svg>
                </button>
              </div>
              <div>
                <div className="w-10">
                  <img
                    src="data:image/png;base64,"
                    alt=""
                    className="w-full rounded-full"
                  />
                </div>
              </div>
              <div className="cursor-pointer">
                {!isDark && (
                  <img
                    src={darkIcon}
                    alt=""
                    className="w-8"
                    onClick={() => changeTheme("dark")}
                  />
                )}
                {isDark && (
                  <img
                    src={ligthIcon}
                    alt=""
                    className="w-8"
                    onClick={() => changeTheme("light")}
                  />
                )}
              </div>
            </li>
          </ul>
        )}

        {isSearchOpen && (
          <div className="flex items-center p-3 space-x-2">
            <button
              onClick={handleBackClick}
              className="text-gray-500 p-2 rounded-full focus:outline-none"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                strokeWidth="1.5"
                stroke={isDark ? "white" : "currentColor"}
                className="w-6 h-6"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  d="M15.75 19.5L8.25 12l7.5-7.5"
                />
              </svg>
            </button>
            <form onSubmit={handleSearch}>
              <input
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                type="text"
                placeholder="Search books..."
                className="outline-none px-2 py-1 rounded-lg "
              />
            </form>
          </div>
        )}
      </nav>
    </div>
  );
}
